tasks.command:
echo "@keyframes shine {
  from {
    background-position: 0% 0%;
  }
  to {
    background-position: -200% 0%;
  }
}

.animate-shine {
  background: linear-gradient(
    90deg,
    #666666 0%,
    #ffffff 50%,
    #666666 100%
  );
  background-size: 200% auto;
  color: transparent;
  -webkit-background-clip: text;
  background-clip: text;
  animation: shine 2s linear infinite;
}" >> app/globals.css
